import { Component } from '@angular/core';
import { elementAt } from 'rxjs';
//importamos nuestras interface
import {Product} from './product.model'
@Component
({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent
{
  //atributos de la clases appcomponnet
  widthImg=10;
  name = 'Sergio';
  age = 28;
  img = 'data:image/png;base64,';
  btnDisabled =true;
  //objeto persona
  person =
  {
    name: 'Sergio',
    age: 18,
    avatar:'data:image/png;base64,'

  }
  //Clase numero 12 trabajando con ngfor
  names: string[] = ['sergio','fernando','prudencio','veliz'];
  newName = '';
  //Clase numero 13 trabajando ng for con objetos
  products: Product [] =
  // = significa asiganacion y el : nos ayuda a hacer el tipado
  [
    {
      name:'Pantalon',
      price: 320,
      image:'./assets/images/adidas.jpeg',
      //nota si agregamos una catacteristica mas debemos hacer que coincida con el modelo de la interface
      //guacho: si algunos productos tiene ese atributo pero otros no entonces preguntamos en la interface
      //entonces ahora si podemos poner una nueva variable
      category: 'Parte de abajo'
    },
    {
      name:'Camisa',
      price: 123,
      image:'./assets/images/adidas.jpeg'

    },
    {
      name:'Tenis Adidas',
      price: 245,
      image:'./assets/images/adidas.jpeg'
    },
    {
      name:'Tenis Nike',
      price: 678,
      image:'./assets/images/adidas.jpeg'
    },
    {
      name:'Tenis Puma',
      price: 234,
      image:'./assets/images/adidas.jpeg'
    },
    {
      name:'Polera adidas',
      price: 100,
      image:'./assets/images/adidas.jpeg'
    },
    {
      name:'Polera Nike',
      price: 130,
      image:'./assets/images/adidas.jpeg'
    },
    {
      name:'Polera Puma',
      price: 150,
      image:'./assets/images/adidas.jpeg'
    },
    {
      name:'Casaca Bolivar',
      price: 400,
      image:'./assets/images/adidas.jpeg'
    },
    {
      name:'Casa Bolivar alterna',
      price: 300,
      image:'./assets/images/adidas.jpeg'
    }

  ]
  //Clase 18 ejemplo con ngStyles
  // creamos el objeto box
  box = {
    width:100,
    height: 100,
    background: 'red'

  }
  //Clase 19 creamos un objeto para los registros
  register=
  {
    name:'',
    email:'',
    password:''

  }
  ToggleButton()
  {
    this.btnDisabled =! this.btnDisabled;

  }
  increaseage()
  {
    this.person.age +=1;
  }
  onScroll(event:Event)
  {
    const element = event.target as HTMLEmbedElement;
    console.log(element.scrollTop);
  }
  changeName (event:Event)
  {
    const element = event.target as HTMLInputElement;
    this.person.name=element.value
  }
  addName ()
  {
    this.names.push(this.newName);
    this.newName= '';
  }
  deleteName(index:number)
  {
    this.names.splice(index,1);
    //lo que ahcemos en esta linea con splice(indice,cuantos valores apartir de ahi)
  }


}

<!--Clase 19 Formularios-->
<form>
  <div classs="input-group">
    <label for="name">Nombre</label>
    <input type="text" required ="name" [(ngModel)]="register.name">
    <p>Mensajes de error</p>
  </div>
  <div classs="input-group">
    <label for="email">Email</label>
    <input type="text" required id="email" [(ngModel)]="register.email">
    <p>Mensajes de error</p>
  </div>
  <div classs="input-group">
    <label for="password">Password</label>
    <input type="text" required id="password" [(ngModel)]="register.password">
    <p>Mensajes de error</p>
  </div>

</form>

<hr/>

<!-- aprendemos Ng class Y ng Styles -->
<h1>ngCLASS ngStyles </h1>
<input type="text" required #nameImput4 = "ngModel" [(ngModel)]="person.name"/>

<hr class="line-error"
  [ngClass]="{
  'valid': nameImput4.valid,
  'invalid': nameImput4.invalid
  }"
/>
<p class="message-error" [class.invalid]="nameImput4.invalid"
>El campo es requerido
</p>
<div class="styles">
  <div>
    <input type="number" [(ngModel)]="box.width"/>
    <input type="number" [(ngModel)]="box.height"/>
    <input type="color" [(ngModel)]="box.background"/>
  </div>
  <div>
    <div [ngStyle]="{
      'width.px': box.width,
      'height.px':box.height,
      'background-color': box.background,
      'display':'block'
    }"></div>

  </div>
</div>

<!--Clases y estilos -->
<h1>Clases y estilos </h1>
<input type="text" required #nameImput2 = "ngModel" [(ngModel)]="person.name"/>
<p class="message-error" [class.invalid]="nameImput2.invalid"
>El campo es requerido
</p>
<label>Nombre</label>
<input type="text" required #nameImput3 = "ngModel" [(ngModel)]="person.name"/>
<p [style.font-style]="nameImput3.invalid ? 'italic':'normal'">Texto Texto Texto</p>
<div class="styles">
  <div>
    <input type="text" [(ngModel)]="widthImg"/>
  </div>
  <div>
    <img [style.width.px]="widthImg" [src]="person.avatar" >

  </div>
</div>

<hr/>
<!-- aprendemos a usar condicionales ng -->
<!-- aprendemos a usar ngSwitch -->
<h1>ngSwitch</h1>
<input type="text" required [(ngModel)]="person.name"/>
<div [ngSwitch]="person.name">
  <p *ngSwitchCase= "'nicolas'">Soy nicolas</p>
  <p *ngSwitchCase= "'camilo'">Soy camilo</p>
  <p *ngSwitchCase= "'sergio'">Soy sergio</p>
  <p *ngSwitchDefault>No hay coincidencias</p>
</div>
<!--- un ejemplo de como se veria sin el switch case
<p *ngIf="person.name === 'nicolas'">Soy nicolas</p>
<p *ngIf="person.name === 'camilo'">Soy camilo</p>
<p *ngIf="person.name === 'sergio'">Soy sergio</p>
-->

<hr/>
<!-- ngFor con objetos -->
<h1>ngFor Objetos</h1>
<!--Clase 15 agregamos estilos -->
<div class="products--grid">
  <div *ngFor="let product of products">
    <img [src]="product.image" alt="image">
    <h2>{{product.price}}</h2>
    <p>{{product.name}}</p>

  </div>
</div>
<hr/>
<!-- ngFor -->
<h1>*ngfor</h1>
<input type="text" required [(ngModel)]="newName"/>
<button (click)="addName()">ADD NAME</button>
<ul>
  <li *ngIf="names.length ===0">NO HAY NOMBRES</li>

  <li *ngFor="let name of names; index as i">
    {{ i+1 }} {{ name }}
    <button (click)="deleteName(i)">delete</button>
  </li>
</ul>
<hr/>
<!-- ngIf -->
<h1>*ngIf</h1>

<p *ngIf="person.name === 'nicolas'">Soy Nicolas</p>
<p *ngIf="person.name === 'Sergio' && person.age === 18 else myblock">Soy Sergio</p>
<ng-template #myblock>
  <p>Bloque de else </p>
</ng-template>

<hr/>
<h1>NgModel</h1>
<p>Nombre: {{person.name}}</p>
<input type="text" required #nameImput = "ngModel" [(ngModel)]="person.name"/>
<p>Valid: {{nameImput.valid}}</p>
<p>Edad: {{person.age}}</p>
<input type="number" max="20" min="10" required #ageImput = "ngModel" [(ngModel)]="person.age"/>
<p>Valid: {{ageImput.valid}}</p>
<hr/>
<h1>Eventos</h1>
<button [disabled]="btnDisabled">Enviar</button>
<button (click)="ToggleButton()">toggleButton</button>
<p>Edad {{person.age}}</p>
<button (click)="increaseage ()">Age ++</button>
<div class="box"(scroll)="onScroll($event)">
  <p>Mi nombre es sergio Fernando prudencio y estoy aprendiendo a programar con angular, vamos por la octava clase y ya estamos viendo eventos como ser la captura de click y scroll: prueba Extraídos de más de 400 mil materias y 55 publicaciones, los resultados del estudio cuestionan la opinión de que la única cosa que las personas quieren son "materias blandengues (Justin Bieber), asuntos rápidos, sin profundidad, o 'artículos- listas' (‘7 cosas que tu debes saber sobre el cabello de Donald Trump’)", dice el director-executivo del API, Tom Rosenstiel.</p>
</div>
<!-- usamos {{}} para mostrar varaibles u objetos en html -->
<p>Nombre: {{person.name}}</p>
<input type="text" [value]="person.name"(keyup)="changeName($event)"/>
<hr/>

<h1>Propiedades</h1>
<button [disabled]="btnDisabled">Enviar</button>
<input type="text" [value]="person.name"/>
<progress max="100" [value]="person.age"></progress>
<img width="100" [src]="person.avatar"/>

<hr/>

<h1>Hola Mundo</h1>
<h2>{{'Hola mundo'.repeat(5)}}</h2>
<p>3+3={{3+3}}</p>
<h3>Hola soy {{ name }} y tengo {{age}}</h3>
<img src ={{img}} alt="image">
